mapper.delete:
DELETE FROM sys_role_permission
WHERE permission_id IN

    #{permissionId}

mapper.select:
WITH RECURSIVE user_perms AS ( -- 直接分配的权限 SELECT p.* FROM sys_permissions p JOIN sys_role_permission rp ON p.permission_id = rp.permission_id JOIN sys_user_role ur ON ur.sys_role_id = rp.role_id WHERE ur.sys_user_id = #{userId}
UNION
-- 递归获取所有子权限 SELECT child.* FROM sys_permissions child JOIN user_perms parent ON child.parent_id = parent.permission_id WHERE child.is_hidden = false -- 排除隐藏权限 ) SELECT * FROM user_perms ORDER BY parent_id NULLS FIRST, sort_order

WITH RECURSIVE permission_tree AS (
SELECT permission_id, parent_id, perm_name FROM sys_permissions WHERE permission_id = #{permissionId}
UNION ALL
SELECT child.permission_id, child.parent_id, child.perm_name FROM sys_permissions child INNER JOIN permission_tree parent ON child.parent_id = parent.permission_id where allow_delete = 0 -- 0 允许删除 ) SELECT permission_id, perm_name FROM permission_tree WHERE permission_id != #{permissionId} ORDER BY permission_id;
